import React from 'react';
import ReactDom from 'react-dom';

function App(){
  const firstName="Heshan";
  const lastName="Sandeepa";
  const date=new Date();
  var str="";
  if(date.getHours()>0 && date.getHours()<12){
    str="Good Morning";
  }
  else if(date.getHours()>=12 && date.getHours()<17){
    str="Good Afternoon";
  }
  else{
    str="Good Night";
  }

  return (
    <h1>Hello {`${firstName} ${lastName} ${str}`}</h1>
  );
}

export default App;
