import React from 'react';
import './App.css';
 

class ToDo extends React.Component{

    constructor(){
        super();
        this.state={
                    checking:this.props.completed
                    }
    }

    handleClick(){
        this.setState((prev)=>{
            if(prev.checking){
                return{
                checking:prev.checking=false
                }
            }
            
            else{
                return{
                    checking:prev.checking=true
                }
            }    
            
        });
    }
    
    render(){
        return(
            <div className="do-items">
                <input type="checkbox" checked={this.props.item.completed} onChange={()=>console.log("Hello")}/>
                <p>{this.props.item.text}</p>   
            </div>
        );
    }
}

export default ToDo;
