import React from 'react';
import './App.css';

function ToDo(props){
    
        return(
            <div className="do-items">
                <input 
                    type="checkbox"
                    checked={props.item.completed} 
                    onChange={()=>props.handleChange(props.item.id)}
                />
                {props.item.completed?<p>{props.item.text}</p>:<b><em>{props.item.text}</em></b>}
            </div>
        )
}
    


export default ToDo;














