import React,{Component} from 'react';

class App extends Component{
  constructor(){
    super();
    this.state={
        firstName:"",
        secondName:"",
        age:""
      }

    this.handleChange=this.handleChange.bind(this);    
  }

  handleChange(event){
    const {name,value}=event.target;
    this.setState({
      [name]:value
    })

  }



  render(){
    return(
      <div> 
        <input name="firstName"  placeholder="First Name" onChange={this.handleChange}/><br />
        <input name="secondName"  placeholder="Last Name" onChange={this.handleChange}/><br />
        <input name="age"  placeholder="Age" onChange={this.handleChange} /><br />
      </div>
    );
  }
}

export default App









