import React,{Component} from 'react';

class App extends Component{
  constructor(){
    super();
    this.state={
        dietary:{
          vegan:false,
          lactose:false,
          kushor:false
        }
    }  
  }




  render(){
    return(
     <div> 
      <label>
        <input type="checkbox"
                name="vegan"
                checked={this.state.dietary.vegan}
                onChange={this.handleChange}
        />Vegan
      </label>

        <label>
        <input type="checkbox"
                name="lactose"
                checked={this.state.dietary.lactose}
                onChange={this.handleChange}
        />Lactose
      </label>

        <label>
        <input type="checkbox"
                name="kushor"
                checked={this.state.dietary.kushor}
                onChange={this.handleChange}
        />kushor
      </label>
    </div>
    )
  }
}

export default App









