import React from 'react';
import Data from './Data';

class App extends React.Component{

    constructor(){
      super();
      this.state={
                   isLoading:true 
                }
      }

    componentDidMount(){
      setTimeout(()=>{
            this.setState(()=>{
              return{
               isLoading:false
              }
            });
      },1500);
    }

    render(){
      return(
        <Data isLoading={this.state.isLoading}/>
      )
    }

}

export default App;






