import React ,{Component} from 'react';
import './MemeStyle.css'

class MemeGenerator extends Component{
    constructor(){
        super();
        this.state={
            topText:"",
            bottomText:"",
            randomImg:"http://i.imgflip.com/1bij.jpg",
            allMemeImgs:[]
        }
        this.handleChange=this.handleChange.bind(this); 
    }

    handleChange(event){
        const {name,value}=event.target;
        this.setState({
            [name]:value
        })
      
    }

    randomPic(){
        const pic=this.state.randomImg.url;
        return(
            pic[Math.floor(Math.random()*pic.length)]
        
            )
    }

        
    componentDidMount(){
        fetch("https://api.imgflip.com/get_memes")
        .then(res=>res.json())
        .then(response=>{
            const {memes}=response.data;
            console.log(memes[0])
            this.setState({
                allMemeImgs:memes
            });
            
        })
    }

    render(){
        
        return(
            <div>
                <form className="meme-form">
                    <input type="text" name="topText" placeholder="Top Text" value={this.state.topText} onChange={this.handleChange}/>
                    <input type="text" name="bottomText" placeholder="Bottom Text" value={this.state.bottomText} onChange={this.handleChange}/> 
                    <button onClick={this.randomPic} onChange={this.randomPic}>Gen</button>
                </form>
                <div className="meme">
                    <img src={this.state.randomImg} alt=""/>
                    <h2 className="top">{this.state.topText}</h2>
                    <h2 className="bottom">{this.state.bottomText}</h2>
                </div>
            </div>
           
        )
    }
}

export default MemeGenerator;
