import React from 'react';
import ReactDom from 'react-dom';

function App(){
  const firstName="Heshan";
  const lastName="Sandeepa";
  const date=new Date();
  var str="";
  const styles={
            fontSize:20
  };

  if(date.getHours()>0 && date.getHours()<12){
    str="Good Morning";
    styles.color="#00FF00";
  }
  else if(date.getHours()>=12 && date.getHours()<17){
    str="Good Afternoon";
    styles.color="#FF00000";    
  }
  else{
    str="Good Night";
    styles.color="#0000FF";
  }

  return (
    <h1 style={styles}>Hello {`${firstName} ${lastName} ${str}`}</h1>
  );
}

export default App;
