import React ,{Component} from 'react';
class App extends Component {
  constructor() {
      super()
      this.state = {
          firstName:"",
          secondName:"",
          age:"",
          gender:"",
          location:"",
          dietaryRestriction:{
              isVegan:false,
              isKosher:false,
              isLactoseFree:false
          }

      }
  this.handleChange=this.handleChange.bind(this);
  }

  handleChange(event){
      const {name,type,checked,value}=event.target;
      type==="checkbox"?this.setState({[name]:checked}):this.setState({[name]:value});    
      }

  render() {
      return (
          <main>
              <form>
                  <input name="firstName"  placeholder="First Name" 
                  onChange={this.handleChange}/><br />
                  <input name="secondName"  placeholder="Last Name" onChange={this.handleChange}/><br />
                  <input name="age"  placeholder="Age" onChange={this.handleChange} /><br />

                  <input type="radio" name="gender" value="male" checked={this.state.gender==="male"} onChange={this.handleChange} />Male

                  <input type="radio" name="gender" value="female" checked={this.state.gender==="female"} onChange={this.handleChange} />Female
                  <br />
                  <br />
                  <select name="location" value={this.state.location} onChange={this.handleChange}>
                      <option value="">Please Select ---</option>
                      <option value="sriLanka">Sri Lanka</option>
                      <option value="australia">Australia</option>
                      <option value="india">India</option>
                  </select>

                  <br />
                  <input type="checkbox" name="isVegan" checked={this.state.dietaryRestriction.isVegan} onChange={this.handleChange}/>Is Vegan
                  <br />
                    <input type="checkbox" name="isKosher" checked={this.state.dietaryRestriction.isKosher} onChange={this.handleChange}/>Is Kosher
                  <br />
                    <input type="checkbox" name="isLactoseFree" checked={this.state.dietaryRestriction.isLactoseFree} onChange={this.handleChange}/>Is Lactose free
                  <br />



                  <button>Submit</button>
              </form>
              <hr />
              <h2>Entered information:</h2>
              <p>Your name: {this.state.firstName} {this.state.secondName}</p>
              <p>Your age: {this.state.age}</p>
              <p>Your gender: {this.state.gender}</p>
              <p>Your destination: {this.state.location}</p>
              <p>
                  Your dietary restrictions: 
                  {this.state.dietaries}
              </p>
          </main>
      )
  }
}

export default App









