import React ,{Component} from 'react';
import toDoData from './toDoData';
import ToDo from './ToDo';
import './App.css'

class App extends Component{

  constructor(){
    super();
    this.state={
                toDos:toDoData
                }
  }

  handleChange(id){
    this.setState((prev)=>{
                    const updatedToDos=prev.toDos.map((item)=>{
                      if(item.id===id){
                        item.completed=!item.completed;
                      }
                      return{
                        item
                      }
                    });

            return {
              toDos:updatedToDos  
            }
                  });

  }

  render(){
    const components=toDoData.map((item)=>{
                  return (<ToDo id={item.id} item={item} handleChange={this.handleChange(item.id)}/>);
                });


    return(
      <div className="do-list">  
        {components}
      </div>  
    );            

  }


}


export default App;







