import React from 'react';

class App extends React.Component{
  constructor(){
    super();
    this.state={
      isFriendly:true
    }
    this.handleChange=this.handleChange.bind(this);
  }

  handleChange(){
    this.setState({
      isFriendly:false
    })
  }
  render(){
    return(
      <div>
        <input type="checkbox" checked={this.state.isFriendly} onChange={this.handleChange}/>
      </div>
    )
  }
}


export default App;









