import React from 'react';
import './App.css';

function ToDo(props){
    
    const completedStyle={
        fontStyle:"italic",
        color:"#cdcdcd",
        textDecoration:"line-through"
    }
        return(
            <div className="do-items">
                <input 
                    type="checkbox"
                    checked={props.item.completed} 
                    onChange={()=>props.handleChange(props.item.id)}
                />
                <p>{props.item.text}</p>
            </div>
        )
}
    


export default ToDo;














